mapper.select:
SELECT EMAIL
FROM TBL_USER
WHERE USER_NO = #{userNo}

SELECT USER_ID
FROM TBL_USER
WHERE USER_NO = #{userNo}

SELECT COUNT(*) > 0
FROM TBL_USER
WHERE USER_NAME = #{nickname}

mapper.update:
UPDATE TBL_USER
SET USER_NAME = #{userName}
WHERE USER_NO = #{userNo}

UPDATE TBL_USER
SET USER_PWD = #{userPwd}
WHERE USER_NO = #{userNo}

mapper.select:
SELECT COUNT(*) > 0
FROM TBL_CHECK
WHERE USER_NO = #{userNo}
AND CHECK_DATE = #{today}

mapper.update:
UPDATE TBL_USER
SET CHECK_ALL_NO = IFNULL(CHECK_ALL_NO, 0) + 1
WHERE USER_NO = #{userNo}

UPDATE TBL_USER
SET LEVEL_NO = #{levelNo}
WHERE USER_NO = #{userNo}

mapper.select:
SELECT IFNULL(CHECK_ALL_NO, 0)
FROM TBL_USER
WHERE USER_NO = #{userNo}

SELECT LEVEL
FROM TBL_LEVEL
WHERE LEVEL_NO = #{levelNo}

mapper.update:
UPDATE TBL_USER
SET USER_IMAGE = #{fileUrl}
WHERE USER_NO = #{userNo}
